    @Override
    public void periodic() {
        // This method will be called once per scheduler run
        
        wheelPos1 = motorController1.getSelectedSensorPosition(0);
        
        wheelPos2 = motorController2.getSelectedSensorPosition(0);
        
        wheelVel1 = motorController1.getSelectedSensorVelocity(0);
        
        wheelVel2 = motorController2.getSelectedSensorVelocity(0);
        
        wheelPos1 *= ((1.0/4096.0)*(1.0/10.0)*(.1016)*Math.PI);
        
        wheelPos2 *= ((1.0/4096.0)*(1.0/10.0)*(.1016)*Math.PI);
        
        wheelVel1 *= ((1.0/4096.0)*(.1016)*Math.PI);
        
        wheelVel2 *= ((1.0/4096.0)*(.1016)*Math.PI);
        
        SmartDashboard.putNumber("pos1: ", wheelPos1);
        
        SmartDashboard.putNumber("pos2: ", wheelPos2);
        
        SmartDashboard.putNumber("vel1: ", wheelVel1);
        
        SmartDashboard.putNumber("vel2: ", wheelVel2);

    }

